}

query MetricsQuery {
  httpMetrics(
    limit: 10,
    filter: {
      tsRange: {begin:"2022-04-27T10:10:10", end:"2022-11-07T21:10:10"}
    },
    aggregate: {sum: bytesSent}
    groupBy: [ts]
    orderBy: [ts_ASC, sum_DESC]
    )
  {
    ts
    sum
  }
}

